definition.script:
pipeline {
    agent {label 'Agent-1'}

    parameters {
        string(name: 'Deploy', defaultValue: 'Dev', description: 'Deploy_env' )
        booleanParam(name: 'Run_Test', defaultValue: true, description:'run_test')
    }
    environment {
        REGION= 'ap-south-1'
        BUILD_VERSION= "1.0.${env.BUILD_NUMBER}"
    }
    stages {
        stage('Print_environment_vars') {
            steps {
                echo "BUILD_NUMBER: ${env.BUILD_NUMBER}"
                echo "Build_Id: ${env.BUILD_ID}"
                echo "Job_Name: ${env.JOB_NAME}"
                echo "Build_Url: ${env.BUILD_URL}"
                echo "Workspace: ${env.WORKSPACE}"
                echo "Node_Name: ${env.NODE_NAME}"
            }
        }
        stage('environment_vars') {
            steps {
                echo "deploy: ${params.Deploy}"
                echo "run_test: ${params.Run_Test}"
            }    
        } 
        stage('Custom_environment_vars') {
            steps {
                echo "Region: ${env.REGION}"
                echo "run_test: ${env.BUILD_VERSION}"
            }    
        } 
        stage('Dynamic_environment_vars') {
            steps {
                script {
                env.RUN_TIME = "runtime-${env.BUILD_NUMBER}"
                echo "RUN_TIME: ${env.RUN_TIME}"
                }
            }    
        } 
    }
}
