definition.script:
pipeline {
    agent any

    tools {
        maven 'maven 3.9'
        jdk 'jdk 17'
    }

    parameters {
        string(name: 'BRANCH_NAME', defaultValue: 'main', description: 'this my repo branch')
        booleanParam(name: 'Run_Test', defaultValue: true, description: 'run test case')
        choice(name: 'build_case', choices: ['debug','release'], description: 'build the repo')
    }

    stages {
        stage('git checkout') {
            steps {
                git branch: "${params.BRANCH_NAME}", url: 'https://github.com/AmlanNayakDevops/Boardgame.git'
            }
        }

        stage('compile') {
            steps {
                echo 'Compiling package'
                sh 'mvn compile'
            }
        }

        stage('test') {
            when {
                expression { return params.Run_Test }
            }
            steps {
                echo 'Running test'
                sh 'mvn test'
            }
        }

        stage('Build') {
            steps {
                echo "Build type selected: ${params.build_case}"
                sh "mvn package -P${params.build_case}"
            }
        }
    }
    post {
        success {
            echo "Pipeline is successful"
        }
        failure {
            echo "Pipeline is fail"
        }
        always {
            echo " Pipeline completed in: ${new Date()} "
        }
    }
}
